mapper.update:
UPDATE idol SET 
	idol_img_ori = #{param2}, 
	idol_img_new = #{param3}
WHERE idol_idx = #{param1}

UPDATE idol SET 
	idol_mark_ori = #{param2}, 
	idol_mark_new = #{param3}
WHERE idol_idx = #{param1}

mapper.select:
SELECT * FROM review r, eat e
WHERE (e.eat_name LIKE #{param1} or r.rev_idx LIKE #{param1}
or r.user_idx LIKE #{param1} or r.rev_content LIKE #{param1} or r.rev_date LIKE #{param1})
and r.eat_idx = e.eat_idx
ORDER BY r.rev_idx;

SELECT * FROM idol 
WHERE idol_name LIKE #{param1} or idol_group LIKE #{param1} or idol_idx LIKE #{param1} 
ORDER BY idol_idx;

SELECT * FROM eat 
WHERE eat_name LIKE #{param1} or idol_idx LIKE #{param1} or eat_idx LIKE #{param1} 
or user_idx LIKE #{param1} or eat_date LIKE #{param1} 
ORDER BY eat_idx;

SELECT * FROM user 
WHERE user_idx LIKE #{param1} or user_nick LIKE #{param1} or user_idx LIKE #{param1} 
or user_name LIKE #{param1} or user_birth LIKE #{param1} 
ORDER BY user_idx;
